                                        function destroyContainers() {
                                            document.getElementById('customer_details_container').style.display = "none";
                                            document.getElementById('customer_fee_container').style.display = "none";
                                            document.getElementById('purchase_token_container').style.display = "none";
                                        }

                                        /**
                                         * searchMeterNo
                                         */
                                        function searchMeterNo() {
                                            destroyContainers();
                                            cleanHiddenValues();

                                            var meterNo = document.getElementById("account_number").value;

                                            var formData = {"meterNo": meterNo};

                                            var url = "{{base_url()}}index.php/NRWB/NRWB/searchMeterNo";

                                            $.ajax({
                                                "type": "POST",
                                                "url": url,
                                                "data": formData,
                                                "dataType": "json",
                                                "success": function (data) {
                                                    var result = JSON.parse(data);
													console.log(result);
                                                    if (result.errorcode === 0) {
                                                        var data = result.data;
                                                        $('#wait_icon').show();
                                                        $("#customer_details_container").show();
                                                        $("#meter_no_error_container").hide();
                                                        $('#wait_icon').hide();

                                                        document.getElementById('customer_number').innerHTML = data.customernumber;
                                                        document.getElementById('customer_identity').innerHTML = data.customeridentity;
                                                        document.getElementById('customer_name').innerHTML = data.customername;
                                                        document.getElementById('customer_type_name').innerHTML = data.customertypename;
                                                        document.getElementById('customer_telephone').innerHTML = data.telephone;
                                                        document.getElementById('customer_state').innerHTML = data.customerstate;
                                                        document.getElementById('customer_address').innerHTML = data.address;
                                                        document.getElementById('customer_meter_number').innerHTML = data.meternumber;
                                                        document.getElementById('meter_no').value = data.meternumber;
                                                        document.getElementById('hidden_meter_no').value = data.meternumber;
                                                        document.getElementById('nrwb_customer_name').value = data.customername;
                                                        document.getElementById('nrwb_customer_phone').value = data.telephone;

                                                        /************************RECEIPT INFO *********************************/
                                                        document.getElementById('hr-customer-type').value = data.customertypename;
                                                        document.getElementById('hr-customer-no').value = data.customernumber;
                                                        document.getElementById('hr-customer-name').value = data.customername;
                                                        document.getElementById('hr-customer-id').value = data.customeridentity;
                                                        document.getElementById('hr-address').value = data.address;
                                                        document.getElementById('hr-plot-number').value = "";
                                                    } else {
                                                        //document.getElementById('customer_details_container').outerHTML = "";
                                                        $("#customer_details_container").hide();
                                                        $("#meter_no_error_container").show();
                                                        $("#customer_fee_container").hide();
                                                        alert('Meter number not found');
                                                    }
                                                },
                                                "error:": function (e) {
                                                    $("#customer_details_container").hide();
                                                    $("#meter_no_error_container").show();
                                                    $("#customer_fee_container").hide();
                                                    alert('Sorry! an error occurred');
                                                }
                                            });
                                        }

                                        /**
                                         * Show Payment Modal
                                         * @returns {undefined}
                                         */
                                        function showPaymentModal() {
                                            $("#payment_modal").modal();
                                        }

                                        /**
                                         * Submit Payment
                                         */
                                        function submitPayment() {
                                            var meterNo = $("#hidden_meter_no").val();
                                            var amount = $("#amount").val();

                                            var convertedAmount = parseFloat(amount);
                                            var requiredStartAmount = parseFloat(499);

                                            if (convertedAmount <= requiredStartAmount) {
                                                alert('Amount should be atleast K500.00');

                                                return;
                                            }

                                            var formData = {"meterNo": meterNo, "amount": convertedAmount};

                                            var url = "{{base_url()}}index.php/NRWB/NRWB/calculateCustomerFee";

                                            $.ajax({
                                                "type": "POST",
                                                "url": url,
                                                "data": formData,
                                                "dataType": "json",
                                                "success": function (data) {
                                                    var result = JSON.parse(data);

                                                    if (result.errorcode === 0) {
                                                        data = result.data;
														console.log(result);
                                                        var chargeCurrency = new Intl.NumberFormat('en-IN', {style: 'currency', currency: 'MWk'}).format(data.chargecurrency);
                                                        var convertedAmountStr = new Intl.NumberFormat('en-IN', {style: 'currency', currency: 'MWk'}).format(convertedAmount);

                                                        document.getElementById('charge_currency').innerHTML = chargeCurrency;
                                                        document.getElementById('charge_volume').innerHTML = data.chargevolume;
                                                        document.getElementById('customer_fee_payment').innerHTML = data.customernumber;
                                                        document.getElementById('payment_fee').innerHTML = new Intl.NumberFormat('en-IN', {style: 'currency', currency: 'MWk'}).format(data.payment);
                                                        document.getElementById('total_vat_amount').innerHTML = new Intl.NumberFormat('en-IN', {style: 'currency', currency: 'MWk'}).format(data.totalvatamount);
                                                        document.getElementById('hidden_amount').value = amount;

                                                        /************************RECEIPT INFO *********************************/
                                                        document.getElementById('hr-purchase-volume').value = data.chargevolume;
                                                        document.getElementById('hr-meter-no').value = meterNo;
                                                        document.getElementById('hr-purchase-amount').value = convertedAmountStr;
                                                        document.getElementById('hr-service-charge').value = chargeCurrency;
                                                        document.getElementById('hr-remaining-debt').value = data.remainingdebt;
                                                        document.getElementById('hr-arrears-recovery').value = "";
                                                        //document.getElementById('addition_fee').innerHTML = data.additionalfee;
                                                        //document.getElementById('total_vat_date').value = data.totalvatrate;
                                                        //document.getElementById('deducation_amount').innerHTML = data.deductionamount;
                                                        //document.getElementById('item_name').innerHTML = data.itemname;
                                                        //document.getElementById('vat_amount').value = data.vatamount;

                                                        $("#customer_details_container").hide();
                                                        $("#meter_no_error_container").hide();
                                                        $("#customer_fee_container").show();
                                                    } else {
                                                        $("#customer_details_container").hide();
                                                        $("#meter_no_error_container").show();
                                                        $("#customer_fee_container").hide();
                                                        alert('Sorry! an error occurred');
                                                    }
                                                },
                                                "error:": function (e) {
                                                    $("#customer_details_container").hide();
                                                    $("#meter_no_error_container").show();
                                                    $("#customer_fee_container").hide();
                                                    alert('Sorry! an error occurred');
                                                }
                                            });
                                        }

                                        /**
                                         * showConfirmTokenModal
                                         * @returns {undefined}
                                         */
                                        function showConfirmTokenModal() {
                                            var requested_meter_no = document.getElementById('hidden_meter_no').value;
                                            var requested_amount = document.getElementById('hidden_amount').value;

                                            var convertedAmount = parseFloat(requested_amount);

                                            var msg = 'You are about to purchase a token of ' + " " +
                                                    new Intl.NumberFormat('en-IN', {style: 'currency', currency: 'MWk'}).format(convertedAmount) + ' for ' + requested_meter_no + ' <br>Are you sure ?';

                                            $("#confirm_token_payment_message").html(msg);

                                            $("#confirm_token_modal").modal();
                                        }

                                        /**
                                         * Purchase Token
                                         */
                                        function purchaseToken() {
                                            hidePurchaseTokenButton();

                                            var meterNo = $("#hidden_meter_no").val();
                                            var amount = $("#hidden_amount").val();
                                            var customerName = $("#nrwb_customer_name").val();
                                            var customerPhone = $("#nrwb_customer_phone").val();
                                            var convertedAmount = parseFloat(amount);

                                            if (customerPhone === null || customerPhone === "") {
                                                customerPhone = "N/A";
                                            }

                                            var formData = {"meterNo": meterNo, "amount":
                                                        convertedAmount, "customerName": customerName, "customerPhone": customerPhone};

                                            var url = "{{base_url()}}index.php/NRWB/NRWB/purchaseToken";

                                            $.ajax({
                                                "type": "POST",
                                                "url": url,
                                                "data": formData,
                                                "dataType": "json",
                                                "success": function (data) {
                                                    var result = JSON.parse(data);
													console.log(result);
                                                    if (result.errorcode === 0) {
                                                        data = result.data.DesiredData.data;
                                                        var vatAmountAsStr = new Intl.NumberFormat('en-IN', {style: 'currency', currency: 'MWk'}).format(data.totalvatamount);

                                                        var groupedToken = data.ststoken.match(/.{1,4}/g);
                                                        var formattedToken = groupedToken.toString().replace(/,/g, '-');
                                                        document.getElementById('nrwb_formatted_token').value = formattedToken;

                                                        document.getElementById('customer_number_token').innerHTML = data.customernumber;
                                                        document.getElementById('charge_volume_token').innerHTML = data.chargevolume;
                                                        document.getElementById('customer_sts_token').innerHTML = formattedToken;
                                                        document.getElementById('invoice_number').innerHTML = data.invoicenumber;
                                                        document.getElementById('customer_purchase_payment').innerHTML = new Intl.NumberFormat('en-IN', {style: 'currency', currency: 'MWk'}).format(data.payment);
                                                        //document.getElementById('total_vat_amount').innerHTML = data.totalvatamount;
                                                        //document.getElementById('total_vat_rate_token').value = data.totalvatrate;
                                                        //document.getElementById('total_vat_amount_token').value = data.vatamount;
                                                        //document.getElementById('addition_fee_token').value = data.additionalfee;
                                                        //document.getElementById('deducation_detail_token').value = data.deductiondetail;
                                                        //document.getElementById('charge_currency_token').value = data.chargecurrency;
                                                        //document.getElementById('customer_trade_id').innerHTML = data.tradeid;
                                                        //document.getElementById('customer_remaining_debt').innerHTML = data.remainingdebt;
                                                        document.getElementById('nrwb_ft').value = result.data.ExtraInfo;

                                                        /************************RECEIPT INFO *********************************/
                                                        document.getElementById('hr-token').value = formattedToken;
                                                        document.getElementById('hr-vat-amount').value = vatAmountAsStr;
                                                        document.getElementById('hr-remaining-debt').value = data.remainingdebt;
                                                        document.getElementById('hr-vat-percent').value = data.totalvatrate;
                                                        document.getElementById('hr-invoice-no').value = data.invoicenumber;

                                                        $("#customer_fee_container").hide();
                                                        $("#purchase_token_container").show();
                                                    } else {
                                                        // show nice alert dialog here displaying result.Response
                                                        alert('Failed to purchase token');
                                                        showPurchaseTokenButton();
                                                    }
                                                },
                                                "error:": function (e) {
                                                    // show alert dialog here displaying result.Response
                                                    alert('Failed to purchase token');
                                                }
                                            });
                                        }

